package com.xiankezu.sirceo.tools;import java.util.List;import android.content.Context;import android.util.Log;import com.loopj.android.http.PersistentCookieStore;/** * 适配3.0的cookie工具类 * * @author wangzhenfei * @date 2014-09-19 * @notice 修改这个类要求讨论 */public class CookieUtils {    public static String fetchCookie(Context c) {        String value = null;        PersistentCookieStore cookieStore = new PersistentCookieStore(c);        List<org.apache.http.cookie.Cookie> cookies = cookieStore.getCookies();        for (org.apache.http.cookie.Cookie cookie : cookies) {            Log.e("cookie", cookie.getName() + ":" + cookie.getValue());            if (cookie.getName().equals("csrftoken")) {                value = cookie.getValue();            }        }        return value;    }}